import React, { useState, useEffect } from "react";
import "./Header.css";
import Logo from "../../assets/logo.jpeg";
import { Button } from "@material-ui/core";
import Modal from "react-modal";
import { Tab } from "@material-ui/core";
import { Tabs } from "@material-ui/core";
import PropTypes from "prop-types";
import { Typography } from "@material-ui/core";
import { FormControl } from "@material-ui/core";
import { Input } from "@material-ui/core";
import { InputLabel } from "@material-ui/core";
import { Link } from "react-router-dom";
import FormHelperText from "@material-ui/core/FormHelperText";
import Register from "../../screens/register/Register";
import Login from "../../screens/login/Login";

// TABPANEL FROM MATERIAL UI
function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Typography component="span">{children}</Typography>}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}

// CUSTOM STYLES FOR LOGIN/REGISTER MODAL
const modalStyle = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

//HEADER FUNCTION
export default function Header(props) {
  //OPEN/CLOSE LOGIN-REGISTER MODAL
  const [isOpen, setIsOpen] = useState(false);
  function openOrCloseModal() {
    setIsOpen(!isOpen);
  }

  //HANDLE MODAL TAB VALUES
  const [tabValue, setTabValue] = React.useState(0);
  const handleTabValueChange = (event, newValue) => {
    setTabValue(newValue);
  };

  //SET USER'S LOGIN STATUS
  const [IsUserLoggedIn, setIsUserLoggedIn] = useState(false);
  useEffect(() => {
    const accessToken = sessionStorage.getItem("access-token");
    if (accessToken) {
      setIsUserLoggedIn(true);
    }
  }, []);

  //UPDATE USER'S LOGIN STATUS & CLOSE MODAL
  const updateLoginStatus = (loggedIn) => {
    setIsUserLoggedIn(loggedIn);
    if (isOpen) {
      openOrCloseModal();
    }
  };

  return (
    <div>
      <div className="header-container">
        <img className="header-logo" src={Logo} alt="logo" />

        {/* DISPLAY LOGIN/LOGOUT BUTTON ACCORDING TO USERS LOGIN STATUS */}
        {IsUserLoggedIn ? (
          <Button
            variant="contained"
            color="secondary"
            style={{ float: "right" }}
            onClick={handleLogoutRequest}
          >
            Logout
          </Button>
        ) : (
          <Button
            variant="contained"
            color="primary"
            style={{ float: "right" }}
            onClick={openOrCloseModal}
          >
            Login
          </Button>
        )}

        {/* LOGIN/REGISTER MODAL */}
        <Modal
          ariaHideApp={false}
          isOpen={isOpen}
          onRequestClose={openOrCloseModal}
          contentLabel="Login-Register Modal"
          style={modalStyle}
          centered
        >
          <Tabs value={tabValue} onChange={handleTabValueChange}>
            <Tab label="Login" {...a11yProps(0)} />
            <Tab label="Register" {...a11yProps(1)} />
          </Tabs>

          {/* LOGIN FORM */}
          <TabPanel value={tabValue} index={0}>
            <Login />
          </TabPanel>

          {/* REGISTRATION FORM */}
          <TabPanel value={tabValue} index={1}>
            <div style={{ textAlign: "center" }}>
              <FormControl required={true}>
                <InputLabel htmlFor="first-name">First Name</InputLabel>
                <Input
                  id="first-name"
                  value={firstName}
                  onChange={({ target }) => setFirstName(target.value)}
                />
                {firstnameIsRequired && (
                  <FormHelperText>
                    <span className="required-error">required</span>
                  </FormHelperText>
                )}
              </FormControl>
              <br />
              <FormControl required={true}>
                <InputLabel htmlFor="last-name">Last Name</InputLabel>
                <Input
                  id="last-name"
                  value={lastName}
                  onChange={({ target }) => setLastName(target.value)}
                />
                {lastnameIsRequired && (
                  <FormHelperText>
                    <span className="required-error">required</span>
                  </FormHelperText>
                )}
              </FormControl>
              <br />
              <FormControl required={true}>
                <InputLabel htmlFor="email-address">Email</InputLabel>
                <Input
                  id="email-address"
                  value={email}
                  onChange={({ target }) => setEmail(target.value)}
                />
                {emailIsRequired && (
                  <FormHelperText>
                    <span className="required-error">required</span>
                  </FormHelperText>
                )}
              </FormControl>
              <br />
              <FormControl required={true}>
                <InputLabel htmlFor="new-password">Password</InputLabel>
                <Input
                  id="new-password"
                  type="password"
                  value={password}
                  onChange={({ target }) => setPassword(target.value)}
                />
                {PasswordIsRequired && (
                  <FormHelperText>
                    <span className="required-error">required</span>
                  </FormHelperText>
                )}
              </FormControl>
              <br />
              <FormControl required={true}>
                <InputLabel htmlFor="contact-no">Contact No.</InputLabel>
                <Input
                  id="contact-no"
                  value={contactNo}
                  onChange={({ target }) => setContactNo(target.value)}
                />
                {contactNoIsRequired && (
                  <FormHelperText>
                    <span className="required-error">required</span>
                  </FormHelperText>
                )}
              </FormControl>
              <br />
              <br />
              <span>{registrationSuccessMsg}</span>
              <br />
              <br />
              <Button
                variant="contained"
                color="primary"
                style={{ float: "center" }}
                type="submit"
                onClick={(e) => {
                  handleRegistrationRequest(e);
                }}
              >
                REGISTER
              </Button>
            </div>
            <Register />
          </TabPanel>
        </Modal>
      </div>
    </div>
  );
}
