import "./Header.css";
import logo from "../../assets/logo.jpeg";
import {
  Box,
  Button,
  Card,
  CardContent,
  FormControl,
  FormHelperText,
  Input,
  InputLabel,
  Tab,
  Tabs,
  Typography,
} from "@material-ui/core";
import Modal from "@material-ui/core/Modal";
import React, { useState, useEffect } from "react";

function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Typography component="span">{children}</Typography>}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}

const modalStyle = {
  top: "50%",
  left: "50%",
  right: "auto",
  bottom: "auto",
  marginRight: "-50%",
  transform: "translate(-50%, -50%)",
};

const Header = () => {
  const [isUserLoggedIn, setIsUserLoggedIn] = useState(false);
  useEffect(() => {
    const accessToken = sessionStorage.getItem("access-token");
    if (accessToken) {
      setIsUserLoggedIn(true);
    }
  }, []);

  const [isOpen, setIsOpen] = useState(false);
  function openOrCloseModal() {
    setIsOpen(!isOpen);
  }

  return (
    <div>
      <div className="header-container">
        <img src={logo} alt="logo loading..." className="header-logo" />
        {isUserLoggedIn ? (
          <Button
            variant="contained"
            className="login-button"
            color="secondary"
          >
            LOGOUT
          </Button>
        ) : (
          <Button
            variant="contained"
            className="login-button"
            color="primary"
            onClick={openOrCloseModal}
          >
            LOGIN
          </Button>
        )}
        <Modal
          ariaHideApp={false}
          isOpen={isOpen}
          onRequestClose={openOrCloseModal}
          contentLabel="Login-Register Modal"
          style={modalStyle}
          centered
        >
          {/*  */}
          <Tabs value={tabValue} onChange={handleTabValueChange}>
            <Tab label="Login" {...a11yProps(0)} />
            <Tab label="Register" {...a11yProps(1)} />
          </Tabs>

          {/* LOGIN FORM */}
          <TabPanel value={tabValue} index={0}>
            <div style={{ textAlign: "center" }}>
              <FormControl required>
                <InputLabel htmlFor="userName">Username</InputLabel>
                <Input
                  id="username"
                  // value={username}
                  // onChange={({ target }) => setUsername(target.value)}
                />
                {/* {usernameIsRequired && (
                  <FormHelperText>
                    <span className="required-error">required</span>
                  </FormHelperText>
                )} */}
              </FormControl>
              <br />
              <FormControl required>
                <InputLabel htmlFor="password">Password</InputLabel>
                <Input
                  id="password"
                  // value={password}
                  // onChange={({ target }) => setPassword(target.value)}
                  type="password"
                />
                {/* {passwordIsRequired && (
                  <FormHelperText>
                    <span className="required-error">required</span>
                  </FormHelperText>
                )} */}
              </FormControl>
              <br />
              <br />
              <Button
                type="submit"
                variant="contained"
                color="primary"
                // onClick={(e) => {
                //   handleLoginRequest(e);
                // }}
                style={{ float: "center" }}
              >
                LOGIN
              </Button>
            </div>
          </TabPanel>
          {/*  */}
        </Modal>
      </div>
    </div>
  );
};

export default Header;
