import React, { useState } from "react";
import RateAppointment from "../appointment/RateAppointment";
import { Paper, Typography, Button } from "@material-ui/core";
import Modal from "react-modal";

Modal.setAppElement(document.getElementById("root"));

const customStyles = {
  content: {
    width: "50%",
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: "0px",
  },
};

const Appointment = ({ isUserLoggedIn }) => {
  const accessToken = sessionStorage.getItem("access-token");
  const userDetails = sessionStorage.getItem("user-info");
  const emailId = sessionStorage.getItem("user-id");

  const [userAppointments, setUserAppointments] = useState([]);
  const getUserAppointments = async () => {
    const url = `http://localhost:8080/users/${emailId}/appointments`;
    const accessToken = sessionStorage.getItem("access-token");

    try {
      const rawResponse = await fetch(url, {
        method: "GET",
        headers: {
          "Content-type": "application/json",
          Accept: "application/json;Charset=UTF-8",
          Authorization: `Bearer ${accessToken}`,
        },
      });

      if (rawResponse.ok) {
        const response = await rawResponse.json();
        setUserAppointments(response);
      } else {
        const error = new Error();
        error.message = "Some Error Occurred";
        throw error;
      }
    } catch (e) {
      alert(e.message);
    }
  };

  useEffect(() => {
    if (accessToken !== "") {
      getUserAppointments();
    }
  }, [emailId]);
  //////////

  const [selectedAppointment, setSelectedAppointment] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModalHandler = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div>
      {!isUserLoggedIn ? (
        <Typography variant="h6" component="h5" className="noLoginText">
          Login to see appointments
        </Typography>
      ) : userAppointments.length === 0 ? (
        <Typography variant="h6" component="h5" className="noLoginText">
          No Current Appointments to Show.
        </Typography>
      ) : (
        <div>
          {userAppointments.map((appointment) => (
            <Paper
              className="appointmentContainer"
              variant="elevation"
              elevation={3}
              key={appointment.appointmentId}
            >
              <Typography
                variant="h6"
                className="hasTextBlack"
                component="h5"
                gutterBottom
              >
                Dr. {appointment.doctorName}
              </Typography>
              <Typography variant="body1" className="hasTextBlack">
                Date: {appointment.appointmentDate}
              </Typography>
              <Typography variant="body1" className="hasTextBlack">
                Symptoms: {appointment.symptoms}
              </Typography>
              <Typography variant="body1" className="hasTextBlack">
                priorMedicalHistory: {appointment.priorMedicalHistory}
              </Typography>
              <br />
              <br />
              <Button
                variant="contained"
                color="primary"
                onClick={() => {
                  setSelectedAppointment(appointment);
                  toggleModalHandler();
                }}
              >
                Rate Appointment
              </Button>
            </Paper>
          ))}
          <Modal
            ariaHideApp={false}
            isOpen={isModalOpen}
            onRequestClose={toggleModalHandler}
            style={customStyles}
          >
            <RateAppointment
              // baseUrl={baseUrl}
              appointment={selectedAppointment}
              toggleModalHandler={toggleModalHandler}
            />
          </Modal>
        </div>
      )}
    </div>
  );
};

export default Appointment;
