import React, { useState, useEffect } from "react";
import { Button } from "@material-ui/core";
import { FormControl } from "@material-ui/core";
import { Input } from "@material-ui/core";
import { InputLabel } from "@material-ui/core";
import FormHelperText from "@material-ui/core/FormHelperText";
const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [usernameIsRequired, setUsernameIsRequired] = useState(false);
  const [passwordIsRequired, setPasswordIsRequired] = useState(false);

  function handleLoginRequest(e) {
    e.preventDefault();
    setUsernameIsRequired(username === "" ? true : false);
    setPasswordIsRequired(password === "" ? true : false);
    const userCredentials = window.btoa(username + ":" + password);
    const loginRequest = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        authorization: "Basic " + userCredentials,
      },
    };
    fetch("/api/v1/auth/login", loginRequest)
      .then((response) => {
        if (!response.ok) {
          return response.json();
        }
        return response;
      })
      .then((response) => {
        if (response.headers) {
          sessionStorage.setItem(
            "access-token",
            response.headers.get("access-token")
          );
          updateLoginStatus(true);
        } else {
          console.log(response.body);
        }
      });
  }

  //FUNCTION FOR LOGGING OUT
  function handleLogoutRequest() {
    const logoutRequest = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        authorization: "Bearer " + sessionStorage.getItem("access-token"),
      },
    };
    fetch("/api/v1/auth/logout", logoutRequest).then((response) => {
      if (response.status === 200) {
        sessionStorage.removeItem("access-token");
        updateLoginStatus(false);
      } else {
        console.log("Invalid access token");
      }
    });
  }

  // const updateLoginStatus = (loggedIn) => {
  //   setIsUserLoggedIn(loggedIn);
  //   if (isOpen) {
  //     openOrCloseModal();
  //   }
  // };

  return (
    <div>
      <div style={{ textAlign: "center" }}>
        <FormControl required>
          <InputLabel htmlFor="userName">Username</InputLabel>
          <Input
            id="username"
            value={username}
            onChange={({ target }) => setUsername(target.value)}
          />
          {usernameIsRequired && (
            <FormHelperText>
              <span className="required-error">required</span>
            </FormHelperText>
          )}
        </FormControl>
        <br />
        <FormControl required>
          <InputLabel htmlFor="password">Password</InputLabel>
          <Input
            id="password"
            value={password}
            onChange={({ target }) => setPassword(target.value)}
            type="password"
          />
          {passwordIsRequired && (
            <FormHelperText>
              <span className="required-error">required</span>
            </FormHelperText>
          )}
        </FormControl>
        <br />
        <br />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          onClick={(e) => {
            handleLoginRequest(e);
          }}
          style={{ float: "center" }}
        >
          LOGIN
        </Button>
      </div>
    </div>
  );
};
export default Login;
