          ariaHideApp={false}
          isOpen={isOpen}
          onRequestClose={openOrCloseModal}
          contentLabel="Login-Register Modal"
          style={modalStyle}
          centered
        >
          <div>asdfasdfasf</div>
        </Modal>
      </div>
    </div>
  );
};

export default Header;
